const { v1 } = require('uuid');
function blankImage() {
    return ["data:image/png;base64,"]
}

const initialState = {
    layers: [{
        src: blankImage(),
        name: "layer 0",
        visible: true,
        locked:true,
        id:v1()
    }],
    activeLayer: 0,
    dpi:1,
    layersCount:1,
}

const canvas = (state = initialState, action) => {
    const {payload} = action
    return {
        ...state,
        ...(typeof payload === "function" ? payload(state) : payload)
    }
}


module.exports = { canvas }
