const { produce } = require('immer')

function blankImage() {
    return ["data:image/png;base64,"]
}

const initialState = {
    layers: [{
        src: blankImage(),
        visible: true
    }],
    activeLayer: 0,
    dpi:window.devicePixelRatio
}

const canvasStore = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_LAYERS':
            return { ...state, layers: action.payload }
        default:
            return state
    }
}

module.exports = { canvasStore }
