const {produce} = require('immer')

function initLayers(){
    return ["data:image/png;base64,"]
}

const initialState = {
    layers: initLayers(),
    ActiveLayer:0
}

const canvasStore = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_LAYERS':
            return {layers:action.payload,ActiveLayer:0}
        default:
            return state
    }
}

module.exports = {canvasStore}
