const { produce } = require('immer')

function blankImage() {
    return ["data:image/png;base64,"]
}

const initialState = {
    layers: [{
        src: blankImage(),
        visible: true,
        name: "layer 0"
    }],
    activeLayer: 0,
    dpi:1
}

const canvasStore = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_LAYERS':
            return { ...state, layers: action.payload }
        case 'SET_DPI':
            return {...state,dpi:action.payload}
        default:
            return state
    }
}

module.exports = { canvasStore }
